DROP TABLE IF EXISTS `areatrigger`;
DROP TABLE IF EXISTS `areatrigger_dbc`; 
CREATE TABLE `areatrigger_dbc` ( `ID` INT NOT NULL DEFAULT '0', `ContinentID` INT NOT NULL DEFAULT '0', `X` FLOAT NOT NULL DEFAULT '0', `Y` FLOAT NOT NULL DEFAULT '0', `Z` FLOAT NOT NULL DEFAULT '0', `Radius` FLOAT NOT NULL DEFAULT '0', `Box_Length` FLOAT NOT NULL DEFAULT '0', `Box_Width` FLOAT NOT NULL DEFAULT '0', `Box_Height` FLOAT NOT NULL DEFAULT '0', `Box_Yaw` FLOAT NOT NULL DEFAULT '0', PRIMARY KEY (`ID`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4; 
